package com.kt.airmap.external.kma.vo.lifeindex;

public class LifeIndexDataVo {

	private String ptDate;
	private String ptTime;
	private String indexCd;
	private String bstorCd;
	private String prctVal;
		
	public String getPtDate() {
		return ptDate;
	}
	public void setPtDate(String ptDate) {
		this.ptDate = ptDate;
	}
	public String getPtTime() {
		return ptTime;
	}
	public void setPtTime(String ptTime) {
		this.ptTime = ptTime;
	}
	public String getIndexCd() {
		return indexCd;
	}
	public void setIndexCd(String indexCd) {
		this.indexCd = indexCd;
	}
	public String getBstorCd() {
		return bstorCd;
	}
	public void setBstorCd(String bstorCd) {
		this.bstorCd = bstorCd;
	}
	public String getPrctVal() {
		return prctVal;
	}
	public void setPrctVal(String prctVal) {
		this.prctVal = prctVal;
	}
}
